package com.osenov.foodtest.ui.menu

import androidx.lifecycle.ViewModel
import com.osenov.foodtest.domain.entity.Advertising
import com.osenov.foodtest.domain.entity.Product
import com.osenov.foodtest.domain.use_case.ProductUseCase
import com.osenov.foodtest.util.Result
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.Flow
import javax.inject.Inject


@HiltViewModel
class ShoppingMenuViewModel @Inject constructor(private val productUseCase: ProductUseCase) :
    ViewModel() {

    suspend fun getProducts() : Flow<Result<List<Product>>>  = productUseCase.execute()

    fun getCategories(products: List<Product>): ArrayList<String> {
        val set = LinkedHashSet<String>()
        for (product in products) {
            set.add(product.category)
        }
        return ArrayList(set)
    }

    fun getCategoryPositions(products: List<Product>): ArrayList<Int> {
        val positions = arrayListOf<Int>()
        var tek: String? = null
        for (i in products.indices) {
            if (tek != products[i].category) {
                tek = products[i].category
                positions.add(i)
            }
        }
        return positions
    }

    fun getAdvertising(): List<Advertising> {
        return listOf(
            Advertising(
                "data:image/jpeg;base64,"
            ),
            Advertising(
                "data:image/jpeg;base64,"
            ),
            Advertising("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQhL9d0cK_Q6BQ94w_efq7ucyDthYThpc8leQ&usqp=CAU"),
            Advertising("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSOkHk7SbJTnzvLZ8gMT0i4qNloTdbQeFf8GA&usqp=CAU"),
            Advertising("https://img.pikbest.com/backgrounds/20210706/yellow-background-creative-fashion-pizza-promotion-propaganda-banner-template_6047757.jpg!c1024wm0"),
            Advertising("data:image/jpeg;base64,")
        )
    }
}